INSERT INTO Autor (autor_nome, autor_data_nascimento, autor_data_falecimento, autor_movimento_literario, autor_biografia, autor_link_foto) VALUES
('Friedrich Nietzsche', '1844-10-15', '1900-08-25', 'Filosofia Moderna', 'Filósofo alemão conhecido por suas críticas à moral tradicional e à religião.', 'https://upload.wikimedia.org/wikipedia/commons/1/1b/Nietzsche187a.jpg'),
('Karl Marx', '1818-05-05', '1883-03-14', 'Economia / Filosofia Política', 'Filósofo e economista alemão, coautor do Manifesto Comunista.', 'https://biografiacorta.net/wp-content/uploads/2022/01/karl.jpg'),
('Marco Aurélio', '0121-04-26', '0180-03-17', 'Estoicismo', 'Imperador romano e filósofo estóico, autor de Meditações.', 'https://cdn.pixabay.com/photo/2017/09/06/14/36/marcus-aurelius-2721715_1280.jpg'),
('Platão', '0427-01-01', '0347-01-01', 'Filosofia Clássica', 'Filósofo grego, discípulo de Sócrates e autor do Mito da Caverna.', 'https://blogdoenem.com.br/apostilas/m2_introducao_a_filosofia_e_sociologia-web-resources/image/figura_05_opt.jpeg'),
('Ichiro Kishimi', '1956-01-01', NULL, 'Filosofia / Psicologia', 'Filósofo japonês, coautor de A Coragem de Não Agradar.', 'https://thecitesite.com/images/authors/ichiro-kishimi.jpeg'),
('Fumitake Koga', '1973-01-01', NULL, 'Literatura contemporânea / Desenvolvimento pessoal', 'Escritor e jornalista japonês, coautor de "A Coragem de Não Agradar". Torna a filosofia adleriana acessível por meio de diálogos inspiradores.', 'https://m.media-amazon.com/images/I/61pZ2JQ-rpL._SX450_CR0%2C0%2C450%2C450_.jpg'),
('Santo Agostinho', '0354-11-13', '0430-08-28', 'Filosofia Cristã', 'Teólogo e filósofo cristão, um dos Pais da Igreja.', 'https://files.jornaluniao.com.br/conteudos/110693.jpg'),
('Alexandre Assaf Neto', '1948-01-01', NULL, 'Finanças corporativas / Educação em administração', 'Economista e professor brasileiro, referência em finanças corporativas, valuation e mercado financeiro; autor de diversas obras didáticas amplamente usadas no Brasil.', 'https://www.fearp.usp.br/images/AAComunica%C3%A7%C3%A3o/PALESTRANTES-ECC-2020-assaf-270x300.png'),
('Ray Dalio', '1949-08-08', NULL, 'Finanças / Investimentos / Desenvolvimento pessoal', 'Investidor e filantropo americano, fundador da Bridgewater Associates e autor de "Princípios", obra sobre liderança, vida e tomada de decisões baseadas em valores claros.', 'https://g4educacao.com/wp-content/uploads/2025/09/68c178c8f7fcc63e4109482d_dalio.jpeg'),
('Ruchir Sharma', '1974-06-09', NULL, 'Economia', 'Economista e investidor indiano, autor de Como os Países Quebram.', 'https://ruchirsharma.com/wp-content/uploads/2019/04/RUCHIR_SHARMA.jpg'),
('Keyu Jin', '1982-11-13', NULL, 'Economia / Macroeconomia internacional', 'Economista chinesa, professora na LSE, especialista na economia da China e nos fluxos macroglobais; autora de “The New China Playbook”.', 'https://pbs.twimg.com/profile_images/920643477456617472/TnMP3OPr_400x400.jpg'),
('Mark Manson', '1984-03-09', NULL, 'Autoajuda / Desenvolvimento Pessoal', 'Escritor e blogueiro americano, autor de A Sutil Arte de Ligar o F*da-se.', 'https://cdn.concreteplayground.com/content/uploads/2024/07/Mark-Manson_01_supplied.jpg'),
('Thiago Nigro', '1990-01-07', NULL, 'Finanças Pessoais', 'Empreendedor e influenciador financeiro brasileiro, criador do canal Primo Rico.', 'https://pixeld.news/wp-content/uploads/elementor/thumbs/Thiago-Nigro-qrxaebqwul0qp1y3tri6wmgjxrcrhn7h80v06d9evc.jpg'),
('Sun Tzu', '-0544-01-01', '-0496-01-01', 'Estratégia / Filosofia Chinesa', 'General e filósofo chinês, autor de A Arte da Guerra.', 'https://tse2.mm.bing.net/th/id/OIP.MVpnN0Bm9HG334_Fcc1TfwHaEJ?cb=12&rs=1&pid=ImgDetMain&o=7&rm=3'),
('Nicolau Maquiavel', '1469-05-03', '1527-06-21', 'Renascimento / Filosofia Política', 'Pensador e diplomata italiano, autor de O Príncipe.', 'https://photos1.blogger.com/x/blogger2/3426/1123834096910560/220/z/321985/gse_multipart37988.jpg'),
('Charles Duhigg', '1974-01-01', NULL, 'Psicologia / Negócios', 'Jornalista e escritor americano, autor de O Poder do Hábito.', 'https://resizing.flixster.com/xCTzQ8qDDLv5EZpDW8W1hqU9nT0=/218x280/v2/https://flxt.tmsimg.com/assets/760515_v9_aa.jpg'),
('Robert Kiyosaki', '1947-04-08', NULL, 'Educação Financeira', 'Empresário e autor de Pai Rico, Pai Pobre.', 'https://i.pinimg.com/736x/ba/eb/1d/baeb1d845b180d7352232af250b62dde.jpg'),
('T. Harv Eker', '1954-06-10', NULL, 'Finanças / Motivacional', 'Autor de Os Segredos da Mente Milionária.', 'https://wiredforyouth.com/wp-content/uploads/2020/04/T.-Harv-Eker.jpg'),
('Morgan Housel', '1984-06-15', NULL, 'Finanças Comportamentais', 'Escritor e colunista americano, autor de A Psicologia Financeira.', 'https://pbs.twimg.com/media/Fthv4yxaEAA5Zlx.jpg'),
('Ana Suy', '1976-01-01', NULL, 'Psicanálise contemporânea', 'Ana Suy é psicanalista, escritora e professora. Autora de livros sobre amor, solidão e desejo, com linguagem poética e acessível. Atua também como coordenadora de grupos de estudo.', 'https://www.cartacapital.com.br/wp-content/uploads/2022/01/ana-suy-1024x617.png'),
('Carl Gustav Jung', '1875-07-26', '1961-06-06', 'Psicanálise / Psicologia Analítica', 'Psicólogo suíço, fundador da psicologia analítica.', 'https://www.socion.cz/wp-content/uploads/Carl-Gustav-Jung.jpg'),
('Alexandre Patricio de Almeida', '1983-01-01', NULL, 'Psicanálise contemporânea', 'Psicanalista, doutor pela PUC-SP, autor de livros sobre psicanálise, ética e cultura. Criador do podcast Psicanálise de Boteco e finalista do Prêmio Jabuti em 2023.', 'https://m.media-amazon.com/images/S/amzn-author-media-prod/j8uargavasci7cvpiv99b0lagq.jpg'),
('Vera Iaconelli', '1965-03-16', NULL, 'Psicanálise contemporânea', 'Psicanalista e escritora, doutora pela USP, atua em temas como parentalidade, maternidade e cultura. Diretora do Instituto Gerar e colunista da Folha de S.Paulo.', 'https://fronteiras.com/storage/thinkers/October2023/PjtN3fZJIjQNQlOz9nwa.jpg'),
('Sigmund Freud', '1856-05-06', '1939-09-23', 'Psicanálise', 'Fundador da psicanálise, autor de A Psicologia do Sonho.', 'https://cdn.britannica.com/58/23358-050-4FB46D99/Sigmund-Freud.jpg'),
('Mariana Caltabiano', '1972-07-21', NULL, 'Literatura infantil e animação', 'Escritora, diretora e roteirista. Criadora de Zuzubalândia, Gui & Estopa e Brasil Animado. Atua na educação infantil com obras criativas e educativas.', 'https://www.soucatarina.com.br/arquivos_personalizados/uploads/noticias/fotos/4156476710205035381761985356394582335815680n_63651cdaee3ca.jpg'),
('Adriano Leite Ribeiro', '1982-02-17', NULL, 'Autobiografia / Esporte', 'Ex-jogador de futebol brasileiro, autor de Adriano: Meu Medo Maior.', 'https://blogdofla.com/wp-content/uploads/2023/03/adriano-imperador.jpg'),
('Phil Knight', '1938-02-24', NULL, 'Empreendedorismo', 'Fundador da Nike e autor de A Marca da Vitória.', 'https://api.time.com/wp-content/uploads/2015/06/phil-knight-nike-chairman.jpg'),
('David McRaney', '1977-04-28', NULL, 'Psicologia / Ciência Cognitiva', 'Escritor americano, autor de Você Não É Tão Esperto Quanto Pensa.', 'https://tedxjackson.com/manage/wp-content/uploads/2018/03/David-McRaney.jpg'),
('Michael Jordan', '1963-02-17', NULL, 'Esporte / Biografia', 'Ex-jogador de basquete, autor de Nunca Deixe de Tentar.', 'https://playersbio.com/wp-content/uploads/2023/07/michael-jordan-siblings.jpg'),
('Robin Williams', '1953-01-01', NULL, 'Design gráfico e tipografia para iniciantes', 'Autora de livros sobre design e tipografia, conhecida por tornar conceitos visuais acessíveis a iniciantes. Vive no deserto do México e escreve sobre design e informática.', 'https://upload.wikimedia.org/wikipedia/commons/7/7e/RobinPatriciaWilliams.jpg'),
('Mortimer J. Adler', '1902-12-28', '2001-06-28', 'Educação / Filosofia', 'Filósofo e educador americano, autor de Como Ler Livros.', 'https://cdn.britannica.com/20/31220-004-88E7BF42/Mortimer-J-Adler-1989.jpg'),
('Ernesto Rodrigues', '1956-01-01', NULL, 'Biografia / Jornalismo', 'Jornalista brasileiro, autor de Ayrton: O Herói Revelado.', 'https://br.web.img3.acsta.net/pictures/15/10/30/18/29/040175.jpg'),
('Aditya Bhargava', NULL, NULL, 'Computação / Algoritmos', 'Engenheiro de software, autor de Entendendo Algoritmos.', 'https://m.media-amazon.com/images/I/51B9HnCqawL._SY600_.jpg'),
('Neal Ford', NULL, NULL, 'Engenharia de Software', 'Arquiteto de software e autor de Arquitetura de Software: as Partes Difíceis.', 'https://i.ytimg.com/vi/VCEgHuA0pos/maxresdefault.jpg'),
('Cory Althoff', NULL, NULL, 'Programação / Carreira', 'Engenheiro de software, autor de O Programador Autodidata.', 'https://tse2.mm.bing.net/th/id/OIP.F5uGRcDBFCzwT7nvsCMGsgHaFW?cb=12&rs=1&pid=ImgDetMain&o=7&rm=3'),
('Jack Widman', '1970-01-01', NULL, 'Programação funcional e matemática', 'PhD em matemática, atua como engenheiro de software há 25 anos. Especialista em programação funcional, também escreve músicas e estuda lógica matemática.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_uYp633LLObPDkgYzdO4moPXAcHYsapaGDA&s'),
('Andrew Hunt', '1964-01-01', NULL, 'Desenvolvimento de Software', 'Cocriador de O Programador Pragmático.', 'https://m.media-amazon.com/images/S/amzn-author-media-prod/nss2a5eho42ssngjjdpfurn96u.jpg'),
('Marco A. Furlan de Souza', '1970-01-01', NULL, 'Engenharia de software e educação tecnológica', 'Engenheiro eletricista e mestre pela USP, atua em ciência da computação com foco em software, algoritmos, sistemas embarcados e educação tecnológica.', 'https://i.pinimg.com/280x280_RS/d1/6b/08/d16b08956101a62c474a417e6bf594c1.jpg'),
('Allen B. Downey', '1967-05-11', NULL, 'Ciência da computação e estatística bayesiana', 'Professor emérito do Olin College, autor de livros sobre Python, estatística e ciência de dados. Doutor em ciência da computação pela UC Berkeley.', 'https://alchetron.com/cdn/allen-b-downey-942cf279-8f02-4b35-ac51-07be64cd9ba-resize-750.jpeg'),
('Thomas Nield', '1985-01-01', NULL, 'Programação e ciência de dados', 'Consultor, escritor e professor na USC. Autor de livros sobre SQL, IA e estatística. Atua com programação reativa, Java, Kotlin e algoritmos de otimização.', 'https://www.oreilly.com/people/wp-content/uploads/sites/4/2020/01/thomas_nield_720x720-0c4a6eba90dbfa3b94e7f0892eda7a5f.jpg'),
('Peter Bruce', '1956-10-02', NULL, 'Estatística / Ciência de Dados', 'Autor de Estatística Prática Para Cientistas de Dados.', 'https://www.faraday.ac.uk/wp-content/uploads/2019/10/Peter-Bruce-profile.jpg');
INSERT INTO Autor (autor_nome, autor_data_nascimento, autor_data_falecimento, autor_movimento_literario, autor_biografia, autor_link_foto) VALUES
('Carlos Drummond de Andrade', '1902-10-31', '1987-08-17', 'Modernismo (2ª geração)', 'Um dos maiores poetas brasileiros, mineiro de Itabira, conhecido por unir lirismo e ironia. Obras como Sentimento do Mundo (1940) e A Rosa do Povo (1945) refletem sua visão existencial e social.', 'https://upload.wikimedia.org/wikipedia/commons/1/17/Foto_Carlos_Drummond_de_Andrade_%28restaurada%29.png'),
('Ítalo Moriconi', '1954-01-01', NULL, 'Pós-modernismo / Crítica literária contemporânea', 'Crítico literário, ensaísta e professor, conhecido por organizar antologias importantes como Os Cem Melhores Poemas Brasileiros do Século. Sua obra analisa identidade, sexualidade e literatura brasileira moderna.', 'https://rascunho.com.br/wp-content/uploads/2024/09/unnamed.png'),
('Manoel de Barros', '1916-12-19', '2014-11-13', 'Modernismo tardio / Poesia neossimbolista', 'Poeta do Pantanal, cultiva uma linguagem simples e inventiva, voltada ao “desimportante”. Livros como O Livro das Ignorãças e Gramática Expositiva do Chão o consagraram.', 'https://rascunho.com.br/wp-content/uploads/2013/02/manoel-de-barros.png'),
('Adélia Prado', '1935-12-13', NULL, 'Pós-modernismo / Poesia confessional', 'Poeta mineira que une religiosidade, cotidiano e sensualidade. Estreou com Bagagem (1976) sob o incentivo de Drummond. Sua poesia é marcada por um lirismo intimista e feminino.', 'https://upload.wikimedia.org/wikipedia/commons/8/84/Adelia_prado_2014_flickr.jpg'),
('Cecília Meireles', '1901-11-07', '1964-11-09', 'Modernismo (1ª geração, vertente lírica)', 'Poeta, educadora e jornalista, uma das maiores vozes líricas do Brasil. Sua obra, como Romanceiro da Inconfidência (1953), combina musicalidade, misticismo e temas universais.', 'https://multi.rio/images/img_2015_06/cecilia_home.jpg'),
('João Cabral de Melo Neto', '1920-01-09', '1999-10-09', 'Modernismo (3ª geração)', 'Poeta pernambucano, conhecido pela objetividade e rigor formal. Morte e Vida Severina é uma de suas obras-primas, unindo poesia e denúncia social.', 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEjvBoksK1jK04mBeOOyzQYfBwtXFf_syKbLElOU33HoXVO704RU7EcCCmdVhY_aIyasiq0oPff5yhHiPzAqHGXn8Lu4jsxe8-6g6qhE2PjivZoMJDZvmTBpPCWJ3o6j4aBpsv4YOhgF7nja/s1600/Jo%25C3%25A3o+Cabral+de+Melo+Neto+%25C2%25A9+Oscar+Cabral.jpg'),
('Fernando Pessoa', '1888-06-13', '1935-11-30', 'Poesia contemporânea / Pós-modernismo', 'Um dos maiores poetas da língua portuguesa. Criou múltiplos heterônimos (como Álvaro de Campos e Ricardo Reis), explorando a fragmentação da identidade e a metafísica da existência.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/32/Pessoa_chapeu.jpg/960px-Pessoa_chapeu.jpg'),
('Ocean Vuong', '1988-10-14', NULL, 'Poesia contemporânea / Lirismo moderno', 'Poeta e romancista vietnamita-americano. Sua obra, como Night Sky with Exit Wounds e o romance On Earth We''re Briefly Gorgeous, aborda memória, guerra, imigração e amor queer.', 'https://indyweek.com/wp-content/uploads/2019/06/6.5-page-Ocean-Vuong-credit-Tom-Hines.jpg'),
('Ada Limón', '1976-03-28', NULL, 'Poesia contemporânea / Confessional', 'Poetisa norte-americana, atual Poet Laureate dos EUA. Sua poesia é emocional, acessível e voltada à natureza, identidade e cura.', 'https://griffinpoetryprize.com/wp-content/uploads/2023/03/Limon-Ada-Limon-scaled.jpg'),
('Danez Smith', '1989-01-01', NULL, 'Poesia contemporânea / Slam / Afro-queer', 'Poeta e performer estadunidense, integrante da cena de poesia falada. Aborda raça, sexualidade e vulnerabilidade.', 'https://static01.nyt.com/images/2019/06/23/us/23sp-danez1/merlin_156057888_fbd34f21-9855-4a05-b980-f54cbb61988e-superJumbo.jpg'),
('Saeed Jones', '1985-11-26', NULL, 'Poesia contemporânea / Slam / Afro-queer', 'Poeta, ensaísta e jornalista norte-americano. Sua obra Prelude to Bruise (2014) e as memórias How We Fight for Our Lives (2019) tratam de identidade racial, sexualidade e sobrevivência.', 'https://upload.wikimedia.org/wikipedia/commons/0/0a/Saeed_Jones_at_BookExpo_%2804832%29.jpg'),
('Claudia Rankine', '1963-01-01', NULL, 'Poesia contemporânea / Poesia documental', 'Poeta e ensaísta renomada, conhecida por Citizen: An American Lyric (2014), que mistura prosa, poesia e ensaio para discutir racismo estrutural e cidadania nos EUA.', 'https://www.americanacademy.de/wp-content/uploads/2022/08/Rankine-Claudia.jpg'),
('Louise Glück', '1943-04-22', '2023-10-13', 'Poesia confessional / Pós-modernismo lírico', 'Laureada com o Prêmio Nobel de Literatura (2020), Glück explorou temas de perda, trauma e renovação em linguagem precisa e austera.', 'https://cdn-test.poetryfoundation.org/cdn-cgi/image/w=2292,h=1528,q=80/content/images/bf43351ac832537dbe27c5b2f350da1c82a51979.jpeg'),
('Terrance Hayes', '1971-11-18', NULL, 'Poesia contemporânea / Pós-modernismo afro-americano', 'Poeta experimental e inovador, criador do “soneto americano”. Sua obra reflete sobre raça, masculinidade e política.', 'https://terrancehayes.com/wp-content/uploads/2013/07/Photo-by-Becky-Thurner-Braddock-1024x685.jpg'),
('Sharon Olds', '1942-11-19', NULL, 'Poesia confessional / Feminismo lírico', 'Uma das mais importantes vozes da poesia confessional moderna. The Dead and the Living (1984) e The Gold Cell (1987) exploram intimidade, corpo e família.', 'https://poemanalysis.com/wp-content/uploads/2023/10/Sharon-Olds-Portrait.jpg'),
('Erika L. Sánchez', '1984-01-01', NULL, 'Poesia latina contemporânea / Literatura chicana', 'Poeta, ensaísta e romancista. Seu livro Lessons on Expulsion (2017) mistura identidade mexicana, feminilidade e migração.', 'https://images.squarespace-cdn.com/content/v1/6297df0b7e376c72ecedbfe7/41a79527-f9e1-4a34-bec6-cc2b998561a3/erika9+(1).jpg'),
('Layli Long Soldier', '1974-01-01', NULL, 'Poesia indígena contemporânea / Experimentalismo', 'Poeta nativa americana que une oralidade, política e descolonização. Seu livro Whereas (2017) confronta o histórico de injustiças contra povos indígenas nos EUA.', 'https://griffinpoetryprize.com/wp-content/uploads/2022/03/2018-Long-Soldier-Layli.jpg'),
('Kaveh Akbar', '1989-01-15', NULL, 'Poesia espiritual contemporânea / Pós-modernismo', 'Poeta iraniano-americano, autor de Calling a Wolf a Wolf (2017) e Pilgrim Bell (2021). Sua obra trata de fé, vício, exílio e identidade.', 'https://www.supporthclib.org/sites/default/files/styles/half_image/public/2025-08/KavehAkbar_creditBeowulfSheehan-web.jpg.webp'),
('Natalie Diaz', '1978-09-04', NULL, 'Poesia indígena contemporânea / Pós-modernismo lírico', 'Poeta e ex-jogadora de basquete profissional. Sua poesia aborda amor, identidade indígena e linguagem.', 'https://cdn.shortpixel.ai/spai/q_glossy/www.nativeartsandcultures.org/wp-content/uploads/2012/12/Natalie-Diaz-headshot.jpg'),
('Rio Cortez', '1984-01-01', NULL, 'Poesia afro-americana contemporânea / Poesia social', 'Poeta e curadora literária, conhecida por Golden Ax (2022), que explora ancestralidade negra, história e resistência.', 'https://images4.penguinrandomhouse.com/author/2261536');
INSERT INTO Autor (autor_nome, autor_data_nascimento, autor_data_falecimento, autor_movimento_literario, autor_biografia, autor_link_foto) VALUES
('George Orwell', '1903-06-25', '1950-01-21', 'Realismo social / Distopia', 'George Orwell foi um escritor e jornalista britânico, autor de “1984” e “A Revolução dos Bichos”, conhecido por suas críticas aos regimes totalitários.', 'https://i.pinimg.com/originals/fa/84/da/fa84da00eb9cf1f0a1bceba90461b515.jpg'),
('Aldous Huxley', '1894-07-26', '1963-11-22', 'Modernismo / Ficção científica', 'Aldous Huxley foi um escritor e filósofo inglês, famoso por “Admirável Mundo Novo”, que aborda o controle social e a perda da individualidade.', 'https://cdn.culturagenial.com/es/imagenes/libro-un-mundo-feliz-de-aldous-huxley-og.jpg'),
('Harper Lee', '1926-04-28', '2016-02-19', 'Realismo / Literatura sulista', 'Harper Lee foi uma escritora americana, conhecida por “O Sol é para Todos”, obra que trata do racismo e da justiça social nos EUA.', 'https://static01.nyt.com/images/2016/02/20/arts/20harperlee3_hp-copy/20harperlee3_hp-copy-superJumbo-v2.jpg'),
('F. Scott Fitzgerald', '1896-09-24', '1940-12-21', 'Geração Perdida / Modernismo', 'Escritor americano que retratou a era do jazz em “O Grande Gatsby” e explorou temas de riqueza, amor e decadência.', 'https://assets.americanliterature.com/images/author/f-scott-fitzgerald.jpg'),
('Jane Austen', '1775-12-16', '1817-07-18', 'Romantismo / Realismo social', 'Romancista inglesa que retratou as relações sociais e de gênero na Inglaterra do século XIX em obras como “Orgulho e Preconceito”.', 'https://biografieonline.it/img/bio/Jane_Austen_1.jpg'),
('Herman Melville', '1819-08-01', '1891-09-28', 'Romantismo / Simbolismo', 'Escritor e marinheiro americano, autor de “Moby Dick”, uma das maiores obras da literatura mundial.', 'https://elfinalde.s3-accelerate.amazonaws.com/2016/02/Herman_Melville.jpg'),
('Liev Tolstói', '1828-09-09', '1910-11-20', 'Realismo russo', 'Escritor russo autor de “Guerra e Paz” e “Anna Kariênina”, obras que exploram a moral, a fé e a sociedade.', 'https://1.bp.blogspot.com/-qHFZkztUDCE/V9IOtXRetvI/AAAAAAAAbqQ/PlhaWWQ3eAANi0pMLUpfaUUDBSupPb5iwCLcB/s1600/MTE5NTU2MzI0OTQxMDA2MzQ3.jpg'),
('Fiódor Dostoiévski', '1821-11-11', '1881-02-09', 'Realismo psicológico', 'Escritor russo conhecido por explorar a mente humana e os dilemas morais em obras como “Crime e Castigo” e “Os Irmãos Karamázov”.', 'https://imagenes.elpais.com/resizer/24ErwSRFUdwE437n5zYvkMNELh8=/1960x0/arc-anglerfish-eu-central-1-prod-prisa.s3.amazonaws.com/public/DL2G2NX2G2LBPFWRAH3T5CHQUI.jpg'),
('J.D. Salinger', '1919-01-01', '2010-01-27', 'Pós-guerra / Existencialismo', 'Autor americano de “O Apanhador no Campo de Centeio”, uma das obras mais influentes da literatura moderna.', 'https://cdn.britannica.com/83/8583-050-9E759F7A/JD-Salinger.jpg'),
('Gabriel García Márquez', '1927-03-06', '2014-04-17', 'Realismo mágico', 'Escritor colombiano e prêmio Nobel de Literatura, autor de “Cem Anos de Solidão”.', 'https://miquelpellicer.com/wp-content/uploads/2014/04/Gabriel-Garc%C3%ADa-M%C3%A1rquez.jpg'),
('J.R.R. Tolkien', '1892-01-03', '1973-09-02', 'Fantasia / Mitopoese', 'Filólogo e escritor britânico, criador da Terra-média e autor de “O Senhor dos Anéis” e “O Hobbit”.', 'https://m.media-amazon.com/images/M/MV5BMGMxMmRkNzctMWQzYy00MTY3LWEzMDAtMzEzMDhkZWI4MjZlXkEyXkFqcGdeQXVyNDUzOTQ5MjY@._V1_.jpg'),
('J.K. Rowling', '1965-07-31', NULL, 'Fantasia / Literatura infantojuvenil', 'Autora britânica da série “Harry Potter”, uma das franquias literárias mais populares do mundo.', 'https://images.mubicdn.net/images/cast_member/23749/cache-699739-1627338129/image-w856.jpg'),
('Khaled Hosseini', '1965-03-04', NULL, 'Ficção contemporânea', 'Escritor afegão-americano, autor de “O Caçador de Pipas”, que aborda amizade, culpa e redenção.', 'https://ychef.files.bbci.co.uk/976x549/p01pmzk4.jpg'),
('Cormac McCarthy', '1933-07-20', '2023-06-13', 'Pós-modernismo / Ficção sombria', 'Autor americano conhecido por “A Estrada” e “Onde os Velhos Não Têm Vez”, com estilo minimalista e temas de violência e moral.', 'https://cdn.britannica.com/74/236874-050-2CAD26F5/Cormac-McCarthy.jpg'),
('Toni Morrison', '1931-02-18', '2019-08-05', 'Literatura afro-americana / Realismo mágico', 'Primeira mulher negra a ganhar o Nobel de Literatura, autora de “Amada” e “O Olho Mais Azul”.', 'https://www.rollingstone.com/wp-content/uploads/2019/08/tonimorrison2.jpg'),
('Margaret Atwood', '1939-11-18', NULL, 'Ficção especulativa / Feminismo', 'Escritora canadense, autora de “O Conto da Aia”, conhecida por explorar temas de poder e gênero.', 'https://biografieonline.it/img/bio/Margaret_Atwood_2.jpg'),
('Vladimir Nabokov', '1899-04-22', '1977-07-02', 'Modernismo / Pós-modernismo', 'Escritor russo-americano, autor de “Lolita”, conhecido por sua prosa refinada e temas psicológicos.', 'https://karsh.org/wordpress/wp-content/uploads/2020/08/Yousuf-Karsh-Vladimir-Nabokov-1972-02.jpg'),
('Charlotte Brontë', '1816-04-21', '1855-03-31', 'Romantismo / Realismo', 'Autora inglesa de “Jane Eyre”, conhecida por explorar a condição feminina e a moralidade.', 'https://www.thoughtco.com/thmb/CWcTWkGFxwofPDnJBSuioFpDlU0=/2808x4081/filters:no_upscale():max_bytes(150000):strip_icc()/GettyImages-2696424-b6e80ec1436a4220aeabe3165dde9946.jpg'),
('Emily Brontë', '1818-07-30', '1848-12-19', 'Romantismo / Gótico', 'Escritora inglesa, autora de “O Morro dos Ventos Uivantes”, uma das maiores obras do romantismo.', 'https://www.biographyicon.com/wp-content/uploads/2024/06/Interesting-Facts-about-Emily-Bronte-768x783.jpg'),
('Oscar Wilde', '1854-10-16', '1900-11-30', 'Esteticismo / Decadentismo', 'Escritor irlandês de “O Retrato de Dorian Gray”, conhecido por seu humor e crítica à hipocrisia social.', 'https://imgblog.interestingfacts.com/2022/08/8-feat-1.jpg'),
('Albert Camus', '1913-11-07', '1960-01-04', 'Existencialismo / Absurdismo', 'Filósofo e escritor francês, autor de “O Estrangeiro” e “A Peste”, defensor do humanismo em tempos de crise.', 'https://th.bing.com/th/id/R.848467ba0f0b1902e00e2ade2b29e49b?rik=qzc%2fcrXnVUjG6w&riu=http%3a%2f%2f1.bp.blogspot.com%2f-_yxOzzOzKYE%2fVLwGix2aSqI%2fAAAAAAABCcM%2flU5jYBIaHmc%2fs1600%2falbert%252Bcamus%252B002_alta.jpg&ehk=Yf1Hz4YWJ0YdOGz0i9UvSk06NeiIado9M8reQl883Ks%3d&risl=&pid=ImgRaw&r=0'),
('Ray Bradbury', '1920-08-22', '2012-06-05', 'Ficção científica / Distopia', 'Escritor americano conhecido por “Fahrenheit 451”, uma crítica à censura e à alienação social.', 'https://cdn.britannica.com/23/200923-050-510EBC89/Ray-Bradbury-1982.jpg'),
('Umberto Eco', '1932-01-05', '2016-02-19', 'Pós-modernismo / Semiótica', 'Filósofo e escritor italiano, autor de “O Nome da Rosa”, que combina mistério, história e filosofia.', 'https://images.gr-assets.com/authors/1588941738p8/1730.jpg'),
('Paulo Coelho', '1947-08-24', NULL, 'Espiritualismo / Realismo mágico', 'Escritor brasileiro de “O Alquimista”, obra sobre autodescoberta e destino, traduzida para dezenas de idiomas.', 'https://d1nslcd7m2225b.cloudfront.net/Pictures/2000x2000fit/7/2/3/1281723_Paulo-Coelho.jpg'),
('Yann Martel', '1963-06-25', NULL, 'Realismo mágico', 'Autor canadense conhecido por unir filosofia e espiritualidade em narrativas simbólicas, como em "Life of Pi".', 'https://www.thefactsite.com/wp-content/uploads/2013/02/yann-martel.jpg'),
('Ian McEwan', '1948-06-21', NULL, 'Realismo psicológico', 'Escritor britânico renomado por romances que exploram dilemas morais e psicológicos, como "Atonement".', 'https://assets.vogue.com/photos/5891629b58aa89a00d5431e8/master/w_2560%2Cc_limit/ian-mcewan.jpg'),
('Alice Walker', '1944-02-09', NULL, 'Feminismo', 'Autora e ativista americana, ganhadora do Pulitzer, conhecida por abordar racismo e gênero em "The Color Purple".', 'https://news.uga.edu/wp-content/uploads/2017/12/Alice-Walker.jpg'),
('Carlos Ruiz Zafón', '1964-09-25', '2020-06-19', 'Romance contemporâneo', 'Escritor espanhol famoso pela série "O Cemitério dos Livros Esquecidos", iniciada com "A Sombra do Vento".', 'https://www.greenme.it/wp-content/uploads/2020/06/Carlos-Ruiz-Zafon.jpg'),
('Neil Gaiman', '1960-11-10', NULL, 'Fantasia contemporânea', 'Autor britânico conhecido por unir mitologia e fantasia moderna, criador de "American Gods" e "Sandman".', 'https://media.npr.org/assets/img/2013/05/16/gaimanauthorphoto_custom-8135a6d7a098351be71dc44947f6cde854345a7b-s1100-c50.jpg'),
('Donna Tartt', '1963-12-23', NULL, 'Ficção contemporânea', 'Escritora americana vencedora do Pulitzer, conhecida por tramas densas e psicológicas como "The Goldfinch".', 'https://cdn.britannica.com/41/258441-050-539E0849/american-writer-donna-tartt-poses-on-red-carpet-at-rome-film-festival-october-19-2015.jpg'),
('Kazuo Ishiguro', '1954-11-08', NULL, 'Ficção literária', 'Autor britânico-japonês, ganhador do Nobel, conhecido por obras sobre memória e identidade como "Never Let Me Go".', 'https://cdn.britannica.com/28/115328-050-4C262676/Kazuo-Ishiguro.jpg'),
('Jeffrey Eugenides', '1960-03-08', NULL, 'Ficção contemporânea', 'Romancista americano premiado por explorar temas de identidade e gênero em "Middlesex".', 'https://media.npr.org/assets/img/2011/10/11/ap110926044428-a0e01f616e67c7ee871f8b5fecf1a972fca71a48-s1200.jpg'),
('Stieg Larsson', '1954-08-15', '2004-11-09', 'Romance policial', 'Jornalista e escritor sueco conhecido pela trilogia "Millennium", iniciada com "The Girl with the Dragon Tattoo".', 'https://images.deepai.org/chat-style-image/f2cc35d1e181485a8dd00378dc7f41f4/stieg-larsson.jpg'),
('Thomas Harris', '1940-09-22', NULL, 'Suspense psicológico', 'Autor americano criador do icônico personagem Hannibal Lecter, destaque em "The Silence of the Lambs".', 'https://101livros.com/wp-content/uploads/2022/11/Thomas-Harris.png'),
('Erin Morgenstern', '1978-07-08', NULL, 'Fantasia contemporânea', 'Autora americana reconhecida por "The Night Circus", uma obra poética e visual sobre magia e amor.', 'https://s26162.pcdn.co/wp-content/uploads/2022/01/Erin-Morgenstern-credit-Adam-Scott.jpg'),
('Mark Haddon', '1962-09-26', NULL, 'Ficção contemporânea', 'Autor britânico e ilustrador, conhecido por explorar neurodiversidade em "The Curious Incident of the Dog in the Night-Time".', 'https://m.media-amazon.com/images/I/C1g-imYBb8S._SY600_.jpg');
INSERT INTO Autor (autor_nome, autor_data_nascimento, autor_data_falecimento, autor_movimento_literario, autor_biografia,  autor_link_foto) VALUES
('Monteiro Lobato', '1882-04-18', '1948-07-04', 'Pré-modernismo / Modernismo', 'Foi um dos maiores escritores brasileiros, pioneiro da literatura infantil no país. Criador do universo do Sítio do Picapau Amarelo, uniu folclore, ciência e cultura popular em suas histórias. Além disso, atuou como editor e defensor da valorização da cultura e das riquezas nacionais.', 'https://s2.glbimg.com/0mM17eK9pZ04RmYNI-hkcqOfNcw=/0x0:755x641/924x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_63b422c2caee4269b8b34177e8876b93/internal_photos/bs/2019/I/F/n3BRxQSsSagA1Q4wxsvg/foto16cul-105-molina-d26.jpg'),
('Ziraldo Alves Pinto', '1932-10-24', '2024-04-06', 'Infantil/juvenil, humor e sátira', 'Foi cartunista, desenhista e escritor brasileiro, reconhecido por suas obras infantis e juvenis. Criou personagens icônicos como O Menino Maluquinho e Flicts, combinando humor, criatividade e crítica social. Também foi cofundador do jornal de humor O Pasquim e deixou grande legado na literatura infantojuvenil brasileira.', 'https://superstarsculture.com/wp-content/uploads/2020/05/Ziraldo-Alves-Pinto.jpg'),
('Ana Maria Machado', '1941-12-24', NULL, 'Infantil/realismo mágico', 'É uma escritora brasileira premiada, conhecida por suas obras de literatura infantil e juvenil. Autora de Bisa Bia, Bisa Bel e História Meio ao Contrário, sua escrita combina imaginação, fantasia e elementos do realismo mágico, sendo referência na renovação da literatura infantojuvenil no Brasil.', 'https://ogimg.infoglobo.com.br/in/25330566-ad5-a41/FT1086A/96768080_SC-Rio-de-Janeiro-RJ-22-12-2021LITERATURAEntrevista-com-a-escritora-e-membro-da-Acad.jpg'),
('Ruth Rocha', '1931-03-02', NULL, 'Infantil/juvenil', 'É uma escritora brasileira de literatura infantil e juvenil, conhecida por obras que estimulam a imaginação e o pensamento crítico das crianças. Autora de Marcelo, Marmelo, Martelo e muitos outros clássicos, é referência na literatura infantil contemporânea do Brasil.', 'https://img.dgabc.com.br/Imagens/2019413114341.jpg'),
('João Carlos Marinho', '1935-09-25', '2019-03-17', 'Infantil/juvenil', 'Foi um escritor brasileiro de literatura infantil e juvenil, conhecido por suas histórias de aventura e mistério. Autor de O Gênio do Crime e da série “Turma do Gordo”, suas obras combinam humor, suspense e crítica social, influenciando gerações de leitores jovens no Brasil.', 'https://p2.trrsf.com/image/fget/cf/774/0/images.terra.com/2019/03/18/1552912160438.jpg'),
('Lúcia Machado de Almeida', '1910-05-09', '2005-04-30', 'Infantil/juvenil', 'Foi uma escritora brasileira de literatura infantil e juvenil, conhecida por suas histórias de mistério e aventura. Autora de obras como O Escaravelho do Diabo e O Caso da Borboleta Atíria, suas narrativas cativaram jovens leitores com suspense e imaginação.', 'https://blogdopco.com.br/wp-content/uploads/2021/03/Lucia-Machado-de-Almeida-Foto-acervo-da-familia-5-657x405.jpg'),
('Maria José Dupré', '1905-05-01', '1984-05-15', 'Romance/infantil', 'Foi uma escritora brasileira voltada para o público infantojuvenil. É autora de A Ilha Perdida, entre outras obras, destacando-se por criar histórias de aventura e fantasia que encantaram gerações de jovens leitores.', 'https://conteudo.solutudo.com.br/wp-content/uploads/2019/09/Maria-Jos%C3%A9-Dupr%C3%A9-2.jpg'),
('José Mauro de Vasconcelos', '1920-02-26', '1984-07-24', 'Romance realista', 'Foi um escritor brasileiro conhecido por suas obras emocionantes para o público infantojuvenil. Autor de O Meu Pé de Laranja Lima, suas histórias destacam sentimentos profundos e experiências da infância, conquistando gerações de leitores.', 'https://es.babelio.com/users/AVT_Jose-Mauro-de-Vasconcelos_571.jpg'),
('Lygia Bojunga', '1932-08-26', NULL, 'Infantil/juvenil', 'É uma escritora brasileira de literatura infantil e juvenil, premiada internacionalmente com o Prêmio Hans Christian Andersen. Autora de A Bolsa Amarela e outras obras, combina fantasia, imaginação e temas sociais, sendo referência na literatura infantojuvenil contemporânea do Brasil.', 'https://www.publishnews.com.br/estaticos/uploads/2017/03/FwKMU0Heajki408C3LSht6H9LBXH50U7tP9yOuLArJn34SSQRR15nZakKHD64dzFkNER9cIllF6LRuW1.jpg'),
('Pedro Bandeira', '1942-03-09', NULL, 'Infantil/juvenil', 'É um escritor brasileiro de literatura infantojuvenil, conhecido por suas histórias de aventura, mistério e romance adolescente. Autor de obras como A Marca de uma Lágrima e a série Os Karas, sua escrita conquistou gerações de jovens leitores no Brasil.', 'https://www.publishnews.com.br/estaticos/uploads/2023/08/jZE19DJc1MRKoYkp9dN9JCvoAEizYq58hZUhN2PLCr0L81AHvpxxyAbU8DrTEsr94SnfhMJcH6KdfWat.jpg'),
('Marçal Aquino', '1958-01-28', NULL, 'Contemporâneo', 'É um escritor, jornalista e roteirista brasileiro. Autor de O Mistério da Cidade-Fantasma, suas obras combinam suspense, narrativa policial e crítica social, conquistando leitores jovens e adultos com histórias instigantes.', 'https://images.mubicdn.net/images/cast_member/124528/cache-347837-1529371356/image-w856.jpg'),
('Jorge Amado', '1912-08-10', '2001-08-06', 'Modernismo', 'Foi um dos maiores escritores brasileiros, conhecido por suas obras que retratam a cultura, o folclore e os problemas sociais da Bahia. Autor de O Gato Malhado e a Andorinha Sinhá e de inúmeros romances, sua literatura mistura realismo, humor e lirismo, conquistando leitores de todas as idades.', 'https://cdn.britannica.com/75/226575-050-A7201A13/Brazilian-author-Jorge-Amado-1988.jpg'),
('Tiago Hakiy', NULL, NULL, 'Indígena/poético', 'É um escritor, poeta e contador de histórias indígena, descendente do povo Sateré-Mawé, nascido em Barreirinha, Amazonas. Sua obra, como O Canto do Uirapuru, mistura mitologia indígena e poesia, celebrando a cultura da floresta e a oralidade tradicional. Formado em Biblioteconomia pela UFAM, Hakiy é reconhecido por sua contribuição à literatura indígena contemporânea do Brasil.', 'https://www.livrariamaraca.com.br/wp-content/uploads/2019/01/tiago-hakiy-e1548081252307.jpg'),
('Alicia Baladan', '1969-01-01', NULL, 'Infantil/ilustrado', 'É uma escritora e ilustradora uruguaia radicada na Itália. Após viver no Brasil e estudar na Accademia di Belle Arti di Brera, em Milão, dedicou-se à ilustração e literatura infantojuvenil. Suas obras, como Uma História Guarani, combinam elementos culturais latino-americanos com uma estética poética e sensível, conquistando reconhecimento internacional. Atualmente, reside e trabalha em Brescia, Itália.', 'https://www.uovonero.com/images/autori/large_Baladan-Alicia.jpg'),
('Maria Clara Machado', '1921-04-03', '2001-04-30', 'Teatro infantil', 'Foi uma dramaturga e escritora brasileira, reconhecida por suas peças de teatro infantil e juvenil. Criadora de obras como O Cavalinho Azul, destacou-se por incentivar a imaginação, a fantasia e o gosto pela leitura e pelo teatro entre crianças e jovens no Brasil.', 'https://lh3.googleusercontent.com/-uH7zI6Atdhw/YGjeOohfbHI/AAAAAAAAJjI/dpcFZNtebyUyDhGS4hZOYne8jc4dYacbgCLcBGAsYHQ/image.png'),
('Raimundo Matos de Leão', '1950-01-01', NULL, 'Literatura infantojuvenil e teatro brasileiro', 'Autor baiano, dramaturgo e professor aposentado da UFBA. Escreve para crianças e jovens, com foco em mitologia, cultura popular e teatro.', 'https://le.com.br/blog/wp-content/uploads/2023/04/RAIMUNDO-MATOS-DE-LEAO.png'),
